    //hàm kiểm tra các điều kiện để nhập cho thỏa mãn
public boolean isEmptyStudent() {
    if (jTextField2.getText().isEmpty()) {
        JOptionPane.showMessageDialog(this, "Student name is required.");
        return false;
    }

    Date dateOfBirth = jDateChooser1.getDate();
    if (dateOfBirth == null) {
        JOptionPane.showMessageDialog(this, "Date of birth is required.");
        return false;
    }
    if (dateOfBirth.compareTo(new Date()) > 0) {
        JOptionPane.showMessageDialog(this, "A future date is not allowed for the date of birth.");
        return false;
    }

    String email = jTextField3.getText();
    if (email.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Email address is required.");
        return false;
    }
    if (!email.matches("^[\\w.%+-]+@[\\w.-]+\\.[a-zA-Z]{2,6}$")) {
        JOptionPane.showMessageDialog(this, "Invalid email address format.");
        return false;
    }

    String phone = jTextField4.getText();
    if (phone.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Phone number is required.");
        return false;
    }
    if (phone.length() >= 15) {
        JOptionPane.showMessageDialog(this, "Phone number is too long.");
        return false;
    }

    if (jTextField5.getText().isEmpty()) {
        JOptionPane.showMessageDialog(this, "Class ID is required.");
        return false;
    }
    if (jTextField6.getText().isEmpty()) {
        JOptionPane.showMessageDialog(this, "Advisor ID is required.");
        return false;
    }
    if (jTextField7.getText().isEmpty()) {
        JOptionPane.showMessageDialog(this, "Major is required.");
        return false;
    }
    if (jTextField8.getText().isEmpty()) {
        JOptionPane.showMessageDialog(this, "Address is required.");
        return false;
    }
    if (imagePath == null) {
        JOptionPane.showMessageDialog(this, "imagepath is required.");
        return false;
    }

    return true;
}

