    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        JFileChooser file = new JFileChooser();
        file.setCurrentDirectory(new File(System.getProperty("user.home")));

        // Tạo bộ lọc FileNameExtensionFilter (chú ý tên đúng là FileNameExtensionFilter)
        FileNameExtensionFilter filter = new FileNameExtensionFilter("*.image", "jpg", "png", "gif");
        file.addChoosableFileFilter(filter);

        // Mở hộp thoại lưu file
        int output = file.showSaveDialog(null);

        if (output == JFileChooser.APPROVE_OPTION) {
            File selectFile = file.getSelectedFile();
            String path = selectFile.getAbsolutePath();
            jLabelImage.setIcon(imageAdjust(path,null));
            imagePath = path;

            // Xử lý đường dẫn file được chọn ở đây
        } else {
            JOptionPane.showMessageDialog(this, "no image is selected");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

