    //kiểm tra email, phone đã tồn tại chưa để update
    public boolean check(){
        String newEmail = jTextField3.getText();
        String newPhone = jTextField4.getText();
        String oldEmail = model.getValueAt(rowIndex, 4).toString();
        String oldPhone = model.getValueAt(rowIndex, 5).toString();
        if(newEmail.equals(oldEmail) && newPhone.equals(oldPhone)){
            return false;
        }else{
            if(!newEmail.equals(oldEmail)){
                boolean x = student.isEmailExist(newEmail);
                if(x){
                  JOptionPane.showMessageDialog(this, "this email is already exist");
                }
                return x;
            }
            if(!newPhone.equals(oldPhone)){
                boolean x = student.isPhoneExist(newPhone);
                if(x){
                  JOptionPane.showMessageDialog(this, "this phone is already exist");
                }
                return x;
            }
        }
        return false;
    }
    
    public boolean checkCourse() {
        int student_id = Integer.parseInt(jTextField9.getText());
        String semester = jComboBox5.getSelectedItem().toString();
        String newCourse = jComboBox6.getSelectedItem().toString();
        String oldCourse = model.getValueAt(rowIndex, 3).toString(); // Sửa oldCourse lấy từ bảng
        String newCourse_id = jTextField25.getText();
        String oldCourse_id = model.getValueAt(rowIndex, 4).toString();

        // Kiểm tra môn học
        if (!newCourse.equals(oldCourse)) {
            // Nếu môn học mới khác với môn học cũ, kiểm tra sự tồn tại của nó
            if (course.isCourseExist(student_id, newCourse, semester)) {
                //JOptionPane.showMessageDialog(this, "The course is already registered for the selected semester.");
                return true; // Môn học mới đã tồn tại
            }
        }

        // Kiểm tra mã môn học
        if (!newCourse_id.equals(oldCourse_id)) {
            // Nếu mã môn học mới khác với mã môn học cũ, kiểm tra sự tồn tại của nó
            if (course.isCourseIdExist(newCourse_id, student_id)) {
                //JOptionPane.showMessageDialog(this, "The course ID is already registered for the selected semester.");
                return true; // Mã môn học mới đã tồn tại
            }
        }

        return false; // Không có thay đổi hoặc không có sự tồn tại trùng lặp
    }
    
