    private void jButton13ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton13ActionPerformed
        // Kiểm tra nếu các trường bắt buộc bị trống
        if (jTextField21.getText().isEmpty() || jComboBox5.getItemCount() == 0 || jTextField9.getText().isEmpty()
                || jComboBox6.getItemCount() == 0 || jComboBox2.getItemCount() == 0 || jTextField23.getText().isEmpty()
                || jTextField25.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "All fields must be filled out.");
            return;
        }

        try {
            // Lấy giá trị từ các thành phần giao diện người dùng
            int id = course.getMax(); // Lấy ID khóa học mới
            int studentId = Integer.parseInt(jTextField9.getText().trim()); // Chuyển chuỗi thành số nguyên
            String courseId = jTextField25.getText().trim();
            String courseName = jComboBox6.getSelectedItem().toString().trim(); // Lấy tên khóa học đã chọn
            String semester = jComboBox5.getSelectedItem().toString().trim(); // Lấy học kỳ đã chọn
            int credit = Integer.parseInt(jComboBox2.getSelectedItem().toString().trim()); // Chuyển chuỗi tín chỉ thành số nguyên
            String instructorId = jTextField23.getText().trim(); // Lấy ID giảng viên

            // Kiểm tra xem mã khóa học đã tồn tại cho sinh viên này chưa
            if (course.isCourseIdExist(courseId, studentId)) {
                JOptionPane.showMessageDialog(this, "This course with the same course ID has already been taken.");
                return;
            }

            // Kiểm tra xem sinh viên đã học môn học này trước đó chưa (bất kể mã khóa học)
            if (course.isCourseExist1(studentId, courseName)) {
                JOptionPane.showMessageDialog(this, "This course has already been taken, even with a different course ID.");
                return;
            }

            // Kiểm tra xem môn học đã được học ở học kỳ này chưa
            if (course.isCourseExist(studentId, courseId, semester)) {
                JOptionPane.showMessageDialog(this, "This course has already been taken in the selected semester.");
                return;
            }

            // Chèn khóa học mới
            if (!course.insertStudentCourse(id, studentId, courseId, courseName, semester)) {
                course.insertCourse(id, studentId, semester, courseName, courseId, instructorId, credit);
            }

            // Cập nhật bảng hiển thị dữ liệu khóa học
            jTable2.setModel(new DefaultTableModel(null, new Object[]{
                "id", "student_id", "course_id", "course", "semester", "instructor_id", "Credit_Hour"}));
            course.getCourseValue(jTable2, ""); // Nạp lại dữ liệu vào bảng

            clearCourse(); // Xóa các trường nhập liệu sau khi thêm khóa học thành công

        } catch (NumberFormatException ex) {
            JOptionPane.showMessageDialog(this, "Invalid input. Please check the values for student ID and credit hour.", "Error", JOptionPane.ERROR_MESSAGE);
        } catch (SQLException ex) {
            Logger.getLogger(Home.class.getName()).log(Level.SEVERE, null, ex);
            JOptionPane.showMessageDialog(this, "An error occurred while fetching or inserting the course data.", "Error", JOptionPane.ERROR_MESSAGE);
        } catch (HeadlessException ex) {
            Logger.getLogger(Home.class.getName()).log(Level.SEVERE, "An error occurred while adding the course", ex);
            JOptionPane.showMessageDialog(this, "An error occurred while adding the course.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton13ActionPerformed

