    public boolean isNumeric(String s){
        try{
            double d = Double.parseDouble(s);
            if(d>=0 && d<=10){
                return true;
            } else {
                JOptionPane.showMessageDialog(this, "Score must be between 0 and 10");
                return false;
            }
        } catch (NumberFormatException ex){
            JOptionPane.showMessageDialog(this,""+ex);
        }
        return false;
    }
    

